    static ArrayList<Shoes> getListData() {
        ArrayList<Shoes> list = new ArrayList<>();
        for (int position = 0; position < ShoesName.length; position++) {
            Shoes shoes = new Shoes();
            shoes.setName(ShoesName[position]);
            shoes.setDetail(ShoesDetail[position]);
            shoes.setPhoto(BrandImage[position]);
            shoes.setShoesPhoto(ShoesImage[position]);
            list.add(shoes);
        }
        return list;
    }
